-- View structure for v_act_task_todo
-- ----------------------------
DROP VIEW [v_act_task_todo]
GO
CREATE VIEW [v_act_task_todo] AS
SELECT A.ID_ AS id,
       A.PROC_INST_ID_ AS processInstanceId,
			 A.PROC_DEF_ID_ AS processDefinitionId,
       A.TASK_DEF_KEY_ AS taskDefinitionKey,
       A.NAME_ AS name,
       A.ASSIGNEE_ AS assignee,
       A.DELEGATION_ AS DELEGATION_ID,
       A.DESCRIPTION_ AS DESCRIPTION,
       A.CREATE_TIME_ AS createTime,
       A.DUE_DATE_ AS DUE_DATE,
       I.USER_ID candidate
  FROM ACT_RU_TASK A
  LEFT JOIN (SELECT DISTINCT * FROM (SELECT TASK_ID_, USER_ID_ USER_ID
                    FROM ACT_RU_IDENTITYLINK I, ACT_RU_TASK T
                      WHERE TASK_ID_ IS NOT NULL
                        AND USER_ID_ IS NOT NULL
                        AND I.TASK_ID_ = T.ID_
                        AND T.ASSIGNEE_ IS NULL
                        AND TYPE_ = 'candidate'
                     UNION
                     SELECT TASK_ID_, U.LOGIN_NAME
                       FROM ACT_RU_IDENTITYLINK I,SYS_USER_ROLE R,SYS_USER U,ACT_RU_TASK T
                      WHERE I.TASK_ID_ IS NOT NULL
                        AND I.GROUP_ID_ IS NOT NULL
                        AND I.TASK_ID_ = T.ID_
                        AND T.ASSIGNEE_ IS NULL
                        AND TYPE_ = 'candidate'
                        AND I.GROUP_ID_ = R.ROLE_ID AND R.USER_ID = U.ID)U) I--候选组和业务上的角色用户表关联
    ON A.ID_ = I.TASK_ID_
GO

